// Importation des hooks useEffect et useState depuis React
import { useEffect, useState } from "react";

function App() {
    // Déclaration de l'état 'users' avec une valeur initiale de tableau vide
    const [users, setUsers] = useState([]);

    // Utilisation de useEffect pour effectuer un appel API lors du montage du composant
    useEffect(() => {
        // Appel API pour récupérer les utilisateurs depuis le serveur local
        fetch("http://localhost:5000/users")
            .then((response) => response.json()) // Conversion de la réponse en JSON
            .then((data) => setUsers(data)) // Mise à jour de l'état 'users' avec les données reçues
            .catch((error) => console.error("Erreur:", error)); // Gestion des erreurs éventuelles
    }, []); // Le tableau vide [] indique que cet effet doit être exécuté une seule fois

    return (
        <div>
            {/* Titre de la liste des utilisateurs */}
            <h1>Liste des utilisateurs</h1>
            <ul>
                {/* Itération sur le tableau 'users' pour créer un élément de liste pour chaque utilisateur */}
                {users.map((user) => (
                    <li key={user.id}>{user.name}</li> // Affichage du nom de l'utilisateur
                ))}
            </ul>
        </div>
    );
}

// Exportation du composant App pour pouvoir l'utiliser dans d'autres fichiers
export default App;
